mapper.select:
select
    ${request.primaryKeyName}
from
    ${request.tableName}
where
    ${request.statusColumnName} = #{status}
order by
    ${request.requestedAtColumnName} asc,
    ${request.primaryKeyName} asc
limit
    #{request.limit}

